<?phpclass DeployBase {	public $projectPath = '';		protected $workDir = '';	protected $directories	= array();	protected $files		= array();	public function readDir( $dir = '.' ) {		$scan = array_diff(scandir($dir), array('..', '.'));				if( $dir === '.' ) {			$dir = '';		} else {			$dir .= '/';		}				foreach( $scan as $s ) {			$path = $dir . $s;			if( is_file($path) ) {				$this->files[] = $path;			} else {				$this->directories[] = $path;				$subdir_found = true;				$this->readDir($path);			}		}	}		protected function createWorkingDirectory() {		$dir = 'protected/work/' . date("ymd");		if( file_exists($dir) ) {			throw new Exception("Working directory already exists!");		}				if( !mkdir($dir, 0777, true) ) {			throw new Exception("Failed to create working directory!");		}		$this->workDir = $dir . '/';	}	public function preDeployScript() {}	public function postDeployScript() {}}